import React from 'react';
import "./css/main.css";
import io from "socket.io-client"

export default class Index extends React.Component{
  constructor(props){
    super(props);

    this.state = {

    }
  }

  componentDidMount(){
    var socket = io();

    socket.on("connected", (data) => {
      // Socket is ready. Tell DataLoader to load data.
      socket.emit("LOAD_HELIX_DATA");
    });
  }


  render(){
    return (
      <div>
        <div id="header">
          <div className="top">
            {/* Logo */}
            <div id="logo">
              <h1 id="title">Twitch Showcase</h1>
            </div>
            {/* Nav */}
            <nav id="nav">
              {/* Put a list here */}
              {this.props.text}
            </nav>
          </div>
        </div>
        {/* Main */}
        <div id="main">
          {/* Intro */}
          <section id="top" className="one dark cover">
            <div className="container">

            </div>
          </section>

          <section id="portfolio" className="two">
            <div className="container">

            </div>
          </section>
        </div>
        {/* Footer */}
        <div id="footer">
          {/* Copyright */}
          <ul className="copyright">
            <li>Built on: Node | Next.js | Webpack | React | Socket.io</li>
          </ul>
        </div>
      </div>

    );
  }
}
